INSERT INTO `posts` (`id`, `title`, `slug`, `cover`, `description`, `content`, `views`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, 'TRỘM BÌNH ẮC QUY ENIMAC BỊ BẮT', 'trom-binh-ac-quy-enimac-bi-bat', '/storage/photos/1/posts/ZbfalxAXszOyA2KOBgSptlmL0cNfNAY2EUJrLbbt.png', 'ĐỐI TƯỢNG TRỘM BÌNH ẮC QUY ENIMAC BỊ BẮT\r\nANTĐ -3 đối tượng trộm cắp 2 bình ắc quy xe tải đã bị Đội CSGT số 8, Phòng CSGT đường bộ - đường sắt, CATP Hà Nội phát hiện truy đuổi. Nhằm tẩu thoát chúng ném lại tang vật ngăn chặn truy đuổi của công an TP HN', '<p><img src="/storage/photos/1/products/fN5yFowoRLhorhbA94RO0IAt69LrYs8QXc3uy8Js.png" alt="" width="774" height="774"></p>\r\n<ol>\r\n<li><span style="color: rgb(224, 62, 45);">V&agrave;o khoảng 5h s&aacute;ng ng&agrave;y 9-4-2016, tổ c&ocirc;ng t&aacute;c do Trung t&aacute; Nguyễn Hồng Hải, Đội ph&oacute; Đội CSGT số 8, Ph&ograve;ng CSGT đường bộ - đường sắt, CATP H&agrave; Nội l&agrave;m nhiệm vụ tuần tra kiểm so&aacute;t đảm bảo ATGT tại ng&atilde; ba Đỗ X&aacute;-Minh Cường thuộc huyện Thường T&iacute;n, ph&aacute;t hiện 3 nam thanh ni&ecirc;n điều khiển xe m&aacute;y được che k&iacute;n BKS từ trong đ&ecirc; ra Quốc lộ 1A c&oacute; biểu hiện nghi vấn đ&atilde; ra hiệu lệnh kiểm tra h&agrave;nh ch&iacute;nh. Thấy CSGT ở ph&iacute;a trước, 3 đối tượng liền quay ngoắt xe bỏ chạy.&nbsp;</span><br><br>Tổ c&ocirc;ng t&aacute;c đ&atilde; d&ugrave;ng m&ocirc; t&ocirc; truy đuổi ph&iacute;a sau. C&aacute;c đối tượng đ&atilde; vứt lại một bao tải dứa h&ograve;ng l&agrave;m vật cản ngăn kh&ocirc;ng cho tổ c&ocirc;ng t&aacute;c đuổi theo rồi ph&oacute;ng mất dạng. Qua kiểm tra, CSGT ph&aacute;t hiện b&ecirc;n trong bao tải dứa c&oacute; 2 b&igrave;nh ắc quy loại b&igrave;nh d&ugrave;ng cho xe tải c&ugrave;ng một số thiết bị kh&aacute;c của xe tải. Đội CSGT số 8 đ&atilde; th&ocirc;ng b&aacute;o tr&ecirc;n VOV giao th&ocirc;ng để t&igrave;m chủ nh&acirc;n của số t&agrave;i sản tr&ecirc;n đến đơn vị nhận lại.<br><a href="http://acquyhoaphat.com/library/uploadfile/images/antd-binh_ac_quy.jpg"><img src="https://acquyhoaphat.com/library/uploadfile/images/antd-binh_ac_quy(1).jpg" alt=""></a><br><em>Anh Quyền vui mừng đến Đội CSGT số 8 để nhận lại số t&agrave;i sản bị mất được CSGT ph&aacute;t hiện, thu giữ trao trả</em><br><br></li>\r\n<li>Đến khoảng 8h s&aacute;ng c&ugrave;ng ng&agrave;y, anh Ho&agrave;ng Văn Quyền (SN 1983, ở Thường T&iacute;n, H&agrave; Nội) đ&atilde; đến Đội CSGT số 8 tường tr&igrave;nh l&agrave; chủ nh&acirc;n của số t&agrave;i sản tr&ecirc;n. Tại đ&acirc;y, anh Quyền cho biết, đ&ecirc;m 8 v&agrave; rạng s&aacute;ng ng&agrave;y 9-4-2016, sau khi l&aacute;i xe tải chở h&agrave;ng xong anh c&oacute; đỗ xe tr&ecirc;n đ&ecirc; s&ocirc;ng Hồng rồi về nh&agrave; ở cạnh đ&oacute; để nghỉ ngơi.&nbsp;<br><br><span style="font-size: 14pt;">Đến khoảng 4h30 ph&uacute;t c&ugrave;ng ng&agrave;y, anh </span>ra kiểm tra xe th&igrave; ph&aacute;t hiện 2 b&igrave;nh ắc quy v&agrave; một bộ đồ chuy&ecirc;n d&ugrave;ng cho xe tải đ&atilde; bị kẻ gian lấy mất. Tổng gi&aacute; trị của số t&agrave;i sản tr&ecirc;n ước chừng hơn 6 triệu đồng. Chưa kịp ra tr&igrave;nh b&aacute;o cơ quan c&ocirc;ng an, anh Quyền nghe thấy th&ocirc;ng b&aacute;o của Đội CSGT số 8 về vụ việc đ&atilde; đến đơn vị nhận lại t&agrave;i sản. Anh Quyền rất x&uacute;c động, viết thư cảm ơn Đội CSGT số 8 đ&atilde; nhanh ch&oacute;ng ph&aacute;t hiện, thu giữ t&agrave;i sản bị kẻ gian lấy mất để trao trả lại cho anh.</li>\r\n</ol>', 2, NULL, '2024-04-05 15:20:29', '2024-04-08 05:36:03');

